INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('Poetics','One of the first books written on what is now called aesthetics. Although parts are lost (e.g., comedy), it has been very influential in western thought, such as the part on tragedy.','https://covers.openlibrary.org/b/id/6528920-S.jpg','https://covers.openlibrary.org/b/id/6528920-M.jpg','Ginn and Company',1536,'0672601680',128,'/works/OL13703833W');
