INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('The Blithedale Romance','Presents an annotated edition of Hawthorne''s novel based on the Brook Farm socialistic experiment which George Ripley started in 1841 with the help of fellow Transcendentalists; and includes a selection of background and source material, and critical essays.','https://covers.openlibrary.org/b/id/8245179-S.jpg','https://covers.openlibrary.org/b/id/8245179-M.jpg','Turtleback Books Distributed by Demco Media',1852,'0393091503',258,'/works/OL455512W');
