INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('The Invisible Man','This book is the story of Griffin, a scientist who creates a serum to render himself invisible, and his descent into madness that follows.','https://covers.openlibrary.org/b/id/6419199-S.jpg','https://covers.openlibrary.org/b/id/6419199-M.jpg','Anaya',1897,'9996444678',182,'/works/OL52266W');
INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('The Blue Castle','In early 1920s Canada, drastic circumstances give Valancy, a twenty-nine-year-old unmarried woman resigned to being an "old maid," the courage to defy her controlling family and escape to a life of her own choosing.','https://covers.openlibrary.org/b/id/369039-S.jpg','https://covers.openlibrary.org/b/id/369039-M.jpg','Independently published',1926,'9798523720123',257,'/works/OL77775W');
INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('When the Sleeper Awakes','A troubled insomniac in 1890s England falls suddenly into a sleep-like trance, from which he does not awake for over two hundred years. During his centuries of slumber, however, investments are made that make him the richest and most powerful man on Earth. But when he comes out of his trance he is horrified to discover that the money accumulated in his name is being used to maintain a hierarchal society in which most are poor, and more than a third of all people are enslaved. Oppressed and uneducated, the masses cling desperately to one dream – that the sleeper will awake, and lead them all to freedom.','https://covers.openlibrary.org/b/id/574886-S.jpg','https://covers.openlibrary.org/b/id/574886-M.jpg','Charles E. Tuttle',1899,'1846375169',278,'/works/OL52151W');
INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('The Last Man','It is the twenty-first century, and England is a republic governed by a ruling elite, one of whom, Adrian, Earl of Windsor, has introduced a Cumbrian boy to the circle. This outsider, Lionel Verney, narrates, a tale of complicated, tragic love, and of the gradual extermination of the human race by plague.','https://covers.openlibrary.org/b/id/882662-S.jpg','https://covers.openlibrary.org/b/id/882662-M.jpg','Gray Rabbit Publications',1826,'9781689922487',414,'/works/OL450124W');
INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('The Handmaid''s Tale','Offred is a Handmaid in the Republic of Gilead. She may leave the home of the Commander and his wife once a day to walk to food markets whose signs are now pictures instead of words because women are no longer allowed to read. She must lie on her back once a month and pray that the Commander makes her pregnant, because in an age of declining births, Offred and the other Handmaids are valued only if their ovaries are viable. Offred can remember the years before, when she lived and made love with her husband, Luke; when she played with and protected her daughter; when she had a job, money of her own, and access to knowledge. But all of that is gone now...','https://covers.openlibrary.org/b/id/8231851-S.jpg','https://covers.openlibrary.org/b/id/8231851-M.jpg','Ghoghnoos Publishing',1985,'8498388015',350,'/works/OL675783W');
INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('The Giver','Given his lifetime assignment at the Ceremony of Twelve, Jonas becomes the receiver of memories shared by only one other in his community and discovers the terrible truth about the society in which he liv','https://covers.openlibrary.org/b/id/8352502-S.jpg','https://covers.openlibrary.org/b/id/8352502-M.jpg','Turtleback Books',1993,'0613047923',200,'/works/OL1846076W');
