INSERT INTO books(title,description,cover_image_small,cover_image_medium,publisher,published_year,isbn,number_of_pages,external_id) VALUES ('Harry Potter and the Prisoner of Azkaban','Harry Potter, along with his best friends, Ron and Hermione, is about to start his third year at Hogwarts School of Witchcraft and Wizardry. Harry can''t wait to get back to school after the summer holidays. (Who wouldn''t if they lived with the horrible Dursleys?) But when Harry gets to Hogwarts, the atmosphere is tense. There''s an escaped mass murderer on the loose, and the sinister prison guards of Azkaban have been called in to guard the school...','https://covers.openlibrary.org/b/id/10580435-S.jpg','https://covers.openlibrary.org/b/id/10580435-M.jpg','Bod ljongs mi dmangs spe skrun khang',1999,'9780329258405',416,'/works/OL82536W');
